var h = require('virtual-dom/h');

login.ref = '/login';
login.render = render;
module.exports = login;

function login(state, ee) {
  ee.on('/login', function() {
    state.set('ref', '/login');
  });
}

function render(state) {
  return h('div', [
    h('h1', 'Login'),
    h('a',{href: '/home'}, 'Home')
  ]);
}